// Helper to get muscles by view
export function getMusclesByView(view: 'front' | 'back'): MusclePath[] {
	return view === 'front' ? FRONT_MUSCLES : BACK_MUSCLES;
}

// Helper to get decorative parts by view
export function getDecorativeByView(view: 'front' | 'back'): DecorativePath[] {
	return view === 'front' ? FRONT_DECORATIVE : BACK_DECORATIVE;
}

// Helper to get body outline by view
export function getBodyOutline(view: 'front' | 'back'): string {
	return view === 'front' ? FRONT_BODY_OUTLINE : BACK_BODY_OUTLINE;
}
